import React from 'react';

const NasaGrid = ({ items }) => {
  return (
    <div className="container py-4 py-xl-5">
      <div className="row mb-5">
        <div className="col-md-8 col-xl-6 text-center mx-auto">
          <h2>NASA Astronomy Pictures</h2>
          <p className="w-lg-50">
            Explore stunning daily space images curated by NASA.
          </p>
        </div>
      </div>
      <div className="row gy-4 row-cols-1 row-cols-md-2 row-cols-xl-3">
        {items.map((item) => (
          <div className="col" key={item.date}>
            <div>
              <img
                className="rounded img-fluid d-block w-100 fit-cover"
                style={{ height: 200, objectFit: 'cover' }}
                src={item.url}
                alt={item.title}
              />
              <div className="py-4">
                <h4>{item.title}</h4>
                <p>{item.explanation.slice(0, 100)}...</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default NasaGrid;
