import React from 'react';

const ApodCard = ({ apod }) => (
  <div className="apod-card">
    <h3>{apod.title}</h3>
    <p>{apod.date}</p>
    {apod.media_type === 'image' ? (
      <img src={apod.url} alt={apod.title} />
    ) : (
      <img src={apod.thumbnail_url || ''} alt="Video Thumbnail" />
    )}
    <p>{apod.explanation}</p>
  </div>
);

export default ApodCard;
