import React, { useState } from 'react';

export default function Navbar({ onFetch }) {
  const [mode, setMode] = useState('today');
  const [count, setCount] = useState(1);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  const handleFetch = () => {
    if (mode === 'today') {
      onFetch('/api/nasa/apod');
    } else if (mode === 'range') {
      if (startDate && endDate) {
        onFetch(`/api/nasa/apod?start_date=${startDate}&end_date=${endDate}`);
      }
    } else if (mode === 'random') {
      onFetch(`/api/nasa/apod?count=${count}`);
    }
  };

  return (
    <div className="p-4 bg-light border-bottom">
      <h4>NASA APOD Viewer</h4>
      <div className="d-flex align-items-end gap-3 mt-3 flex-wrap">
        <select
          className="form-select w-auto"
          value={mode}
          onChange={(e) => setMode(e.target.value)}
        >
          <option value="today">Today</option>
          <option value="range">Date Range</option>
          <option value="random">Random</option>
        </select>

        {mode === 'range' && (
          <>
            <input
              type="date"
              className="form-control"
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
            />
            <input
              type="date"
              className="form-control"
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)}
            />
          </>
        )}

        {mode === 'random' && (
          <input
            type="number"
            min="1"
            className="form-control w-auto"
            value={count}
            onChange={(e) => setCount(e.target.value)}
          />
        )}

        <button onClick={handleFetch} className="btn btn-primary">
          Fetch
        </button>
      </div>
    </div>
  );
}
